package com.lego.admin.formydaddy.activityimport android.os.Bundleimport android.support.design.widget.NavigationViewimport android.support.v4.view.GravityCompatimport android.support.v7.app.ActionBarDrawerToggleimport android.view.MenuItemimport com.arellomobile.mvp.MvpAppCompatActivityimport com.arellomobile.mvp.presenter.InjectPresenterimport com.lego.admin.formydaddy.Rimport com.lego.admin.formydaddy.presenter.MainPresenterimport com.lego.admin.formydaddy.presenter.MainViewimport kotlinx.android.synthetic.main.activity_play.*import kotlinx.android.synthetic.main.app_bar_play.*class PlayActivity : MvpAppCompatActivity(), NavigationView.OnNavigationItemSelectedListener, MainView {    @InjectPresenter    lateinit var mPresenter: MainPresenter    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_play)        setSupportActionBar(toolbar)        val toggle = ActionBarDrawerToggle(this, drawerMenu, toolbar, R.string.navigation_drawer_open, R.string.navigation_drawer_close)        drawerMenu.addDrawerListener(toggle)        toggle.syncState()        navigationView.setNavigationItemSelectedListener(this)    }    override fun onBackPressed() {        if (drawerMenu.isDrawerOpen(GravityCompat.START)) {            drawerMenu.closeDrawer(GravityCompat.START)        } else {            super.onBackPressed()        }    }    override fun onNavigationItemSelected(item: MenuItem): Boolean {        // Handle navigation view item clicks here.        when (item.itemId) {            R.id.nav_game -> {                mPresenter.stopGame()                mPresenter.startGame(this)            }            R.id.nav_stats -> mPresenter.showStats(this)            R.id.nav_help -> mPresenter.showRules(this)            R.id.nav_exit -> finish()            else -> { }        }        drawerMenu.closeDrawer(GravityCompat.START)        return true    }}