package com.lego.admin.formydaddy.logicimport android.util.Pairimport java.util.*class Game {    val DECK_SIZE = 28    var tree: MutableList<Node> = ArrayList()    fun Game() {        initGameDeck()        shuffle()        initGameTree()    }    fun initGameDeck() {        var tempType: Int = 0        loop@ for (tempValue in 0..DECK_SIZE) {            var newNode = Node()            when (tempValue){                7 -> tempType++                13 -> tempType++                18 -> tempType++                22 -> tempType++                25 -> tempType++                27 -> tempType++            }            newNode.value = Pair(tempType, tempValue)            tree.add(newNode)        }    }    fun shuffle() {        tree.sort()    }    fun initGameTree() {    }    fun printTree() {//        for (i in tree.indices) {//            Log.d("PRINT TREE", tree[i].id)//            if (tree[i].getParent() != null) {//                for (j in 0..tree[i].getParent().size() - 1)//                    Log.d("PRINT TREE", "Parent -" + tree[i].getParent().get(j).getId())////            }//            if (tree[i].getChildren() != null) {//                for (j in 0..tree[i].getChildren().size() - 1)//                    Log.d("PRINT TREE", "Children -" + tree[i].getChildren().get(j).getId())//            }//            Log.d("PRINT TREE", "---------------------")//        }    }}