package com.lego.admin.formydaddy.logicimport android.util.Pairimport android.widget.ImageButtonimport java.util.*import kotlin.collections.ArrayListdata class Node(var mother: Node? = null, var father: Node? = null) : Comparable<Node> {    var id: String = ""    var value: Pair<Int, Int>? = null    lateinit var View: ImageButton    var children: MutableList<Node> = ArrayList()    fun getParent(): Pair<Node?, Node?> {        return Pair(mother, father)    }    fun isAlive(): Boolean {        return father != null || mother != null || children.isEmpty()    }    override fun compareTo(other: Node): Int {        val random = Random()        return random.nextInt() / 28  //game deck size    }}